        while (j&gt;0 &amp;&amp; arr[j] &lt; arr[j-1]) {
            // swap
            arr[j-1] = arr[j] + arr[j-1] - (arr[j] = arr[j-1]);
            j--;
        }
    }

    System.out.println(Arrays.toString(arr));
}
